mapper.select:
select m.mib_cd, m.ord_cd, pr.pro_type, pr.pro_name, o.ord_vol, w.wh_name, m.mib_info_status, m.mib_date, u.user_name from MINBOUND m left join ORDERS o on m.ord_cd = o.ord_cd left join PRODUCT pr on pr.pro_cd = o.pro_cd left join WAREHOUSE w on w.wh_cd = pr.wh_cd left join USER u on m.user_id = u.user_id
m.mib_cd like CONCAT('%',#{search1},'%') and pr.pro_name like CONCAT('%',#{search2},'%')
and m.mib_date between #{search3} and #{search4} and m.mib_date <= #{search4} and m.mib_date >= #{search3}
and mib_info_status = 0 and mib_info_status = 1
order by mib_cd desc limit #{startRow},#{pageSize}

select p.pib_cd, p.per_cd, pe.actual_completion_amount, pr.pro_name, w.wh_name, p.pib_info_status, p.pib_date, u.user_name from PINBOUND p join PERFORMANCE pe on p.per_cd = pe.per_cd join INSTRUCTION i on pe.ins_cd = i.ins_cd join PRODUCT pr on pr.pro_cd = i.pro_cd join WAREHOUSE w on w.wh_cd = pr.wh_cd left join USER u on p.user_id = u.user_id
p.pib_cd like CONCAT('%',#{search5},'%') and pr.pro_name like CONCAT('%',#{search6},'%')
and p.pib_date between #{search7} and #{search8} and p.pib_date <= #{search8} and p.pib_date >= #{search7}
and pib_info_status = 0 and pib_info_status = 1
order by pib_cd desc limit #{startRow},#{pageSize}

select sum(pro_vol)
from PRODUCT
where wh_cd = #{wh_cd}

mapper.update:
update MINBOUND
set mib_info_status = 1,
	mib_date = now(),
	user_id = #{user_id}
where mib_cd = #{mib_cd}

update PRODUCT
set pro_vol = pro_vol + #{ord_vol}
where pro_cd = #{pro_cd}

update ORDERS
set ord_info_status = 2
where ord_cd = #{ord_cd}

update PINBOUND
set pib_info_status = 1,
	pib_date = now(),
	user_id = #{user_id}
where pib_cd = #{pib_cd}

update PRODUCT
set pro_vol = pro_vol + #{actual_completion_amount}
where pro_cd = #{pro_cd}

mapper.select:
select o.*, c.*, pr.*, w.*, u.* from OUTBOUND o join CONTRACT c on o.con_cd = c.con_cd join PRODUCT pr on pr.pro_cd = c.pro_cd join WAREHOUSE w on w.wh_cd = pr.wh_cd left join USER u on o.user_id = u.user_id
o.ob_cd like CONCAT('%',#{search1},'%') and pr.pro_name like CONCAT('%',#{search2},'%')
and o.ob_date between #{search3} and #{search4} and o.ob_date <= #{search4} and o.ob_date >= #{search3}
and ob_info_status = 0 and ob_info_status = 1
order by ob_cd desc limit #{startRow},#{pageSize}

mapper.update:
update OUTBOUND
set ob_info_status = 1,
	ob_date = now(),
	user_id = #{user_id}
where ob_cd = #{ob_cd}

update PRODUCT
set pro_vol = pro_vol - #{con_vol}
where pro_cd = #{pro_cd}

update CONTRACT
set con_info_status = 2
where con_cd = #{con_cd}
