mapper.update:
	update WAREHOUSE w
	left join PRODUCT p
	on w.WH_CD = p.WH_CD
	SET w.WH_CD = #{warehouseCode}, w.WH_NAME = #{warehouseName}, w.WH_AREA = #{warehouseArea}, w.WH_MVOL = #{warehouseMvol}, w.WH_LOCAL = #{warehouseLocal}, w.WH_TEL_NUMBER = #{warehouseTelNumber}, sum(p.PRO_VOL) = #{productSvol}
  where w.WH_CD = #{warehouseCode};

mapper.select:
select p.PRO_CD "productCode", p.PRO_NAME "productName", p.PRO_TYPE "productType", p.PRO_VOL "productVol", w.WH_CD "warehouseCode", w.WH_NAME "warehouseName", w.WH_AREA "warehouseArea", w.WH_MVOL "warehouseMvol" from WAREHOUSE w inner join PRODUCT p on w.WH_CD = p.WH_CD
p.PRO_CD like concat('%', #{productCode},'%')
and p.PRO_NAME like concat('%', #{productName}, '%')
and w.WH_NAME like concat('%', #{warehouseName}, '%')
and p.PRO_TYPE = #{productType}
order by p.PRO_CD desc limit #{startRow}, #{pageSize}

mapper.update:
update PRODUCT
set PRO_VOL = #{productVol}
WHERE PRO_CD = #{productCode}

mapper.select:
select PRO_VOL as "productVol"
from PRODUCT
where PRO_CD = #{productCode}

mapper.update:
update PRODUCT
set PRO_VOL = #{realAmount}
where PRO_CD = #{productCode}

mapper.select:
select PRO_VOL as "productVol"
from PRODUCT
where PRO_CD = #{productCode}
