mapper.select:
select m.mib_cd, m.ord_cd, pr.pro_type, pr.pro_name, o.ord_vol, w.wh_name, m.mib_info_status, m.mib_date, m.user_id from MINBOUND m join ORDERS o on m.ord_cd = o.ord_cd join PRODUCT pr on pr.pro_cd = o.pro_cd join WAREHOUSE w on w.wh_cd = pr.wh_cd
m.mib_cd like CONCAT('%',#{search1},'%') and pr.pro_name like CONCAT('%',#{search2},'%')
and m.mib_date between #{search3} and #{search4} and m.mib_date <= #{search4} and m.mib_date >= #{search3}
and mib_info_status = 0 and mib_info_status = 1
order by mib_cd desc limit #{startRow},#{pageSize}

select p.pib_cd, p.per_cd, pe.actual_completion_amount, pr.pro_name, w.wh_name, p.pib_info_status, p.pib_date, p.user_id from PINBOUND p join PERFORMANCE pe on p.per_cd = pe.per_cd join INSTRUCTION i on pe.ins_cd = i.ins_cd join PRODUCT pr on pr.pro_cd = i.pro_cd join WAREHOUSE w on w.wh_cd = pr.wh_cd
p.pib_cd like CONCAT('%',#{search5},'%') and pr.pro_name like CONCAT('%',#{search6},'%')
and p.pib_date between #{search7} and #{search8} and p.pib_date <= #{search8} and p.pib_date >= #{search7}
and pib_info_status = 0 and pib_info_status = 1
order by pib_cd desc limit #{startRow},#{pageSize}

select o.*, c.*, pr.*, w.* from OUTBOUND o join CONTRACT c on o.con_cd = c.con_cd join PRODUCT pr on pr.pro_cd = c.pro_cd join WAREHOUSE w on w.wh_cd = pr.wh_cd
o.ob_cd like CONCAT('%',#{search1},'%') and pr.pro_name like CONCAT('%',#{search2},'%')
and o.ob_date between #{search3} and #{search4} and o.ob_date <= #{search4} and o.ob_date >= #{search3}
and ob_info_status = 0 and ob_info_status = 1
order by ob_cd desc limit #{startRow},#{pageSize}
